.timeline::before {
  content: "";
  background: #ea2429;
  width: 3px;
  height: 100%;
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
}

.timeline-item {
  width: 100%;
}

.timeline-item:nth-child(even) .timeline-content {
  float: right;
}

.timeline-item:nth-child(even) .timeline-content .date {
  right: auto;
  left: 0;
}

.timeline-item:nth-child(even) .timeline-content::after {
  content: "";
  position: absolute;
  border-style: solid;
  width: 0;
  height: 0;
  top: 23px;
  left: -14px;
  border-width: 10px 15px 10px 0;
  border-color: transparent #eeeeee transparent transparent;
}

.timeline-item::after {
  content: "";
  display: block;
  clear: both;
}

.timeline-content {
  width: 46%;
  background: #eeeeee;
  position: relative;
}

.timeline-content::after {
  content: "";
  position: absolute;
  border-style: solid;
  width: 0;
  height: 0;
  top: 23px;
  right: -14px;
  border-width: 10px 0 10px 15px;
  border-color: transparent transparent transparent #eeeeee;
}

.timeline-img {
  width: 16px;
  height: 16px;
  background: #fff;
  border-radius: 50%;
  position: absolute;
  left: 50%;
  margin-top: 25px;
  margin-left: -8px;
  border: 3px solid #ea2429;
}

@media screen and (max-width: 920px) {
  .timeline::before {
    left: 50px;
  }

  .timeline .timeline-img {
    left: 50px;
  }

  .timeline .timeline-content {
    max-width: 100%;
    width: auto;
    margin-left: 70px;
    margin-bottom: 1rem;
  }

  .timeline .timeline-item:nth-child(even) .timeline-content {
    float: none;
  }

  .timeline .timeline-item:nth-child(odd) .timeline-content::after {
    content: "";
    position: absolute;
    border-style: solid;
    width: 0;
    height: 0;
    top: 23px;
    left: -14px;
    border-width: 10px 15px 10px 0;
    border-color: transparent #eeeeee transparent transparent;
  }
}
