.year {
  text-align: center;
  background: white;
  position: relative;
  padding: 30px;
  margin: 0;
}
@media screen and (max-width: 920px) {
  .year {
    text-align: left;
    padding-left: 0;
  }
}

@media screen and (min-width: 720px) and (max-width: 920px) {
  .timeline {
    position: relative;
  }

  .timeline::before, .timeline .timeline-img {
    left: 30px;
  }
