text_lines:
@media (min-width: 45rem){
.block-link {
    font-size: .833rem;
}
}

.percentage-wrapper {
    display: flex;
}
.percentage-icon {
    padding-right: 20px;
	margin: 0;
	flex: 25%;
max-width: 25%;
}
.percentage-title {
	font-size: 35px;
    font-weight: 900;
    line-height: 1.2;
margin: auto;
}
.percentage-desc {
	font-size: 16px;
    font-weight: 900;
	margin-left: 1rem;
    margin-top: auto;
    margin-bottom: auto;
}
.percentage-content {
    flex-direction: row;
    display: flex;
    flex: 75%;
max-width: 75%
}
.layout__region--third .percentage-content {
    flex-direction: column;
    display: flex;
}
.layout__region--third .percentage-desc {
	margin-left: unset;
}
@media (min-width: 45rem) {
.percentage-content {
    flex-direction: column;
    display: flex;
}
.percentage-title {
margin: initial;
}
.percentage-desc {
	margin-left: unset;
margin-top: initial;
    margin-bottom: initial;
}
}
@media (min-width: 57.5rem) {
.percentage-icon {
width: auto;
height: auto;
}
.percentage-title {
	font-size: 2.2rem;
    font-weight: 900;
margin: initial;
}
.percentage-desc {
	font-size: 1.25rem;
  font-weight: 900;
	margin-left: unset;
    margin-top: initial;
    margin-bottom: initial;
}
.percentage-content {
    flex-direction: column;
    display: flex;
}
}

