    display: none;
}
    div#block-stickyapplynowbutton .field.field--name-body.field--type-text-with-summary.field--label-.field__item.text-long {
        padding: 0 !important;
    }

   div#block-stickyapplynowbutton img {
        max-width:100px;

    }

