const roadmapElem = document.querySelectorAll('.roadmap');

    roadmapElem.forEach(elem => {
        const toggleButtons = elem.querySelectorAll('.lightbox-toggle');

        toggleButtons.forEach(toggleButton => {
            toggleButton.addEventListener('click', function(e) {
                e.preventDefault();
                const extraContent = elem.querySelector('.roadmap__content__item__extra[data-for="' + this.id + '"]');
                extraContent.classList.toggle('open');
                document.querySelector('body').classList.toggle('roadmap-open');

                elem.querySelectorAll('.roadmap__content__item__extra').forEach(extra => {
                    if (extra !== extraContent) {
                        extra.classList.remove('open');
                    }
                });

                const currentActive = elem.querySelector('.roadmap__content__item.active');
                if (currentActive) {
                    currentActive.classList.remove('active');
                }
                toggleButton.closest('.roadmap__content__item').classList.add('active');

                elem.querySelector('.roadmap__backdrop').classList.toggle('open');
            });
        });

        const closeButtons = elem.querySelectorAll('.roadmap__content__item__extra__close');

        closeButtons.forEach(closeButton => {
            closeButton.addEventListener('click', function(e) {
                e.preventDefault();
                this.closest('.roadmap__content__item__extra').classList.remove('open');
                document.querySelector('body').classList.remove('roadmap-open');

                const activeItem = this.closest('.roadmap').querySelector('.roadmap__content__item.active');
                if (activeItem) {
                    activeItem.classList.remove('active');
                }
                elem.querySelector('.roadmap__backdrop').classList.remove('open');
            });
        });

        const modalBackdrop = elem.querySelector('.roadmap-backdrop-a');

        modalBackdrop.addEventListener("click", function(event) {
            const openExtras = elem.querySelectorAll(".roadmap__content__item__extra.open");

            if (openExtras.length === 0) return; // Exit if no element has .open

            openExtras.forEach(extra => extra.classList.remove("open"));

            document.querySelector('body').classList.remove('roadmap-open');

            const activeItem = elem.querySelector('.roadmap__content__item.active');
            if (activeItem) {
                activeItem.classList.remove('active');
            }

            elem.querySelector('.roadmap__backdrop').classList.remove('open');
        });
    });

