        .event-area.flex-less .event-items .item {
            display: block;
        }

        .event-area.flex-less .event-items .equal-height {
            margin-bottom: 30px;
            margin-left: 0;
            list-style: none;
            padding: 0 10px;
        }

        .event-area .event-items .item {
            background: #fff;
            display: flex;
            box-shadow: 0 0 5px #cccccc;
            margin-bottom: 20px;
            position: relative;
            padding: 15px 15px 10px 250px;
            min-height: 170px;
        }

        .event-area .event-items .item .thumb {
            position: absolute;
            width: 220px;
            height: 140px;
            top: 15px;
            left: 15px;
            background-size: cover;
            background-repeat: no-repeat;
            background-position: center center;
        }

        .event-area .event-items .item .thumb img {
            max-width: 100%;
            min-height: 100%;
            object-fit: cover;
            border-radius: 15px;
        }

        .event-area .event-items .item .info-box {
            padding: 0 5px;
            position: relative;
            display: flex;
        }

        .event-area .event-items .item .info-box .date {
            font-family: 'Poppins', sans-serif;
            text-align: center;
            min-width: 75px;
            text-transform: uppercase;
            font-weight: 600;
            letter-spacing: 0.5px;
            margin-top: -3px;
            background: #f7f7f7;
            position: absolute;
            right: 20px;
            top: -40px;
            padding: 10px;
            border-radius: 10px;
            font-size: 12px;
            border: 1px solid #ccc;
            display: none;
        }

        .event-area .event-items .item .info-box .date strong {
            font-size: 40px;
            font-weight: 800;
            line-height: 1;
            color: #EA2429;
            display: block;
        }

        .event-area .event-items .item .info-box .content {
            position: relative;
            z-index: 1;
            width: 100%;
        }

        .event-area .event-items .item .info-box .content h4 {
            font-size: 20px;
            font-weight: 600;
            margin-bottom: 15px;
            line-height: 1.4;
            margin-top: 5px;
            display: -webkit-box;
            -webkit-box-orient: vertical;
            -webkit-line-clamp: 1;
            overflow: hidden;
            color: #162c39;
            margin: 0 0 7px;
        }

        .event-area .event-items .item .info-box .content h4 a {
            color: #101010;
            text-decoration: none;
        }

        .event-area .event-items .item .info-box .content ul li {
            display: inline-block;
            margin-right: 12px;
            padding-right: 15px;
            position: relative;
            z-index: 1;
            font-size: 15px;
            margin: 0 0 8px;
            display: block;
            padding: 0;
        }

        .event-area .event-items .item .info-box .content ul li i {
            color: #EA2429;
            margin-right: 2px;
            font-size: 18px;
            position: relative;
            top: 2px;
            width: 20px;
            text-align: center;
            margin-left: 0px;
        }

        i.fas.fa-clock {
            font-weight: 500;
        }

        .event-area .event-items .item .info-box .content p {
            display: -webkit-box;
            -webkit-box-orient: vertical;
            -webkit-line-clamp: 1;
            overflow: hidden;
            margin-bottom: 15px;
        }

        .event-area .event-items .item .info-box .content ul {
            margin-bottom: 15px;
            padding: 0;
            margin-left: 0;
        }

        .event-area .event-items .item .info-box .content ul li:last-child {
            margin: 0;
        }

        .event-area .event-items .item .info-box .content ul li:last-child::after {
            display: none;
        }

        .event-area .event-items .item .info-box .content .bottom {
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin-top: 15px;
        }

        .event-area .event-items .item .info-box .content .bottom a:last-child {
            text-transform: capitalize;
            font-weight: 600;
        }

        .event-area .event-items .item .info-box .content .bottom a:last-child i {
            color: #EA2429;
            margin-right: 5px;
        }

        .event-area .more-btn {
            margin-top: 50px;
            float: left;
            width: 100%;
        }

        .event-area.flex-less .more-btn {
            margin-top: 20px;
