                                var startDateTime1 = new Date(data.data[i].calendarSlotDetails[j].slotStartDate).toLocaleTimeString(navigator.language, { hour: '2-digit', minute: '2-digit' });
                                var endDateTime1 = new Date(data.data[i].calendarSlotDetails[j].slotEndDate).toLocaleTimeString(navigator.language, { hour: '2-digit', minute: '2-digit' });

                                slotString1 += "<div class='col-lg-12'><div class='slot-layout'><div class='slot-layout-heading'>" + data.data[i].calendarSlotDetails[j].sessionName + "</div><div class='row'><div class='col-sm-9'><div class='single-meta'><span><i class='fa fa-calendar'></i> " + startdate1[2] + " " + startdate1[1] + " " + startdate1[3] + "</span><span><i class='fa fa-clock-o'></i> " + startDateTime1 + " - " + endDateTime1 + "</span><span><i class='fa fa-map-marker'></i> " + data.data[i].calendarSlotDetails[j].slotLocation + "</span></div></div><div class='col-sm-3 text-right'><a href='https://alumni.singaporetech.edu.sg/view-event/?id=" + data.data[i].calendarSlotDetails[j].id + ";slotId=" + data.data[i].calendarSlotDetails[j].slotId + "' target='_blank' class='slot-view-btn'>View More</a></div></div></div></div>";
                            }
                            console.log(slotString1);
                            var calenderimage1 = data.data[i].calendarImageUrl ? data.data[i].calendarSlotDetails[0].calendarImageUrl : 'https://alumni.singaporetech.edu.sg/api-items/img/events-defalut-img.jpg'
                            var ul = document.getElementById("event-list");
