                            li.className = "col-sm-12";
                            var div = document.createElement("div");
                            div.className = "item";
                            div.innerHTML =
                                "<div class='thumb'><img src=" + calenderimage1 + " alt='Thumb'  decoding='sync'></div>" +
                                "<div class='info'><div class='info-box'>" +
