$(document).ready(function() {
    //Hide see more link after click
    $("a.ckeditor-readmore-toggler").click(function(event) {
        $(this).hide();
    })
    if ($('.programme-menu-sidebar__menu').length) {
        //Hide Student Stories
        $(".programme-menu-sidebar__menu li > a:contains('Student stories')").remove();
        //Replace ISWP anchor link
        $(".page-node-type-programme .programme-menu-sidebar__menu a[href='#iwsp']").text($("#iwsp h2").text());
    }
    //Remove Fees and Finances when it's empty
    if ($('.page-node-type-programme').length) {
        if (!$('#fees-and-finances .group-tabs-fees-and-finances').length) {
            $('#fees-and-finances').hide();
        }
    }
    //Alumni Mentoring Programme - Alumni Mentors
    if ($("#cluster_filter").length) {
        //Alumni Mentors Slider
        $('.slick__slider').slick({
            dots: false,
            infinite: false,
            speed: 300,
            slidesToShow: 4,
            slidesToScroll: 4,
            prevArrow: '<nav class="slick__arrow" role="navigation"><button type="button" data-role="none" class="slick-prev slick-arrow" aria-label="❮" style="display: inline-block;" aria-disabled="true">❮</button></nav>',
            nextArrow: '<nav class="slick__arrow" role="navigation"><button type="button" data-role="none" class="slick-next slick-arrow" aria-label="❯" aria-disabled="false" style="">❯</button></nav>',
            responsive: [{
                breakpoint: 720,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            }]
        });
        //Alumni Mentors Filter
        var filtered = false;
        $('#cluster_filter').on('change', function() {
            $('.slick__slider').slick('slickUnfilter').slick('slickGoTo', 0).slick('slickFilter', '.' + $('#year_filter').val() + '.' + this.value);
        });
        $('#year_filter').on('change', function() {
            $('.slick__slider').slick('slickUnfilter').slick('slickGoTo', 0).slick('slickFilter', '.' + $('#cluster_filter').val() + '.' + this.value);
        });
    }
    //Alumni Landing Page - SITizen Past Issue
    if ($("#sitizenissue").length) {
        $('#sitizenissue').on('change', function() {
            if (this.value != "") window.open(this.value, '_blank ');
        });
    }
    //Wrap table responsive
    $(".simple-paragraph table").wrap('<div class="responsive-table"><div class="responsive-table__wrap"></div></div>');

    if($("#block-head-search-button").length == 0)
    {
        $("#block-head-menu-button").css("margin-left", "auto");
    }
});
