}
.special-button:hover {
    text-decoration: none;
    background-color: rgba(35, 31, 32, .85);
	color: #fff;
}	
.single_info .button_icon {
	position: absolute;
	right: 0;
	top: 50%;
	width: 60px;
	height: 100%;
	background-color: rgba(34, 34, 34, .09);
	text-align: center;
	transform: translateY(-50%);
	line-height: 100%;
	display: flex;
	align-items: center;
	justify-content: center
}	
.single_info{display:inline-block; margin-left:10px; margin-bottom:10px;}

.get-social{color:#fff;}
    .get-social .title {
  font-size: 18px;
  margin-bottom: 20px;
  font-weight: 700;
}
.get-social .title-2 {
  font-size: 50px;
  line-height: 1.2;
  margin-bottom: 40px;
  font-weight: 300;
}
.get-social .desc {
    padding-left: 40px;
    position: relative;
  }
.get-social .desc:before {
    content: '';
    display: block;
    width: 4px;
    height: 85px;
    position: absolute;
    left: 0;
    top: 0;
    background: white;
  }
.get-social .social {
  margin-top: 30px;
}
.get-social .social a {
  text-decoration: none;
  color: white;
  display: inline-block;
  margin-right: 40px;
  width: 50px;
  height: 50px;
  background: #222;
  border-radius:50%;
  line-height: 50px;
  text-align: center;
  font-size: 24px
}

.get-social .social a .icon {
  height: 20px;
}

