/* Hide this component */
    #block-webmastercustomcss {
        display: none;
    }

    #block-webmastercustomjs {
        display: none;
    }

    /* Page with 50/50 image and text (Use background:contain in CMS) */
    @media screen and (min-width: 720px) {
        .layout--suite--twocols.layout--column-gap--remove.layout--background-size--contain .layout__region.layout__region--second {
            display: flex;
            align-items: center;
            justify-content: center;
            background: #eeeeee;
            padding: 0 5rem !important;
        }
    }
    @media screen and (max-width: 720px) {
        .layout--twocol.layout--background-size--contain.layout--column-gap--added.layout--column-width--50-50.layout--align-items--start .grid--layout--suite {
            display: flex;
            flex-wrap: wrap;
            flex-direction: column-reverse;
        }
    }
    .layout--twocol.layout--background-size--contain.layout--column-gap--added.layout--column-width--50-50.layout--align-items--start .grid--layout--suite .layout__region--second {
        margin-bottom: 1.5rem;
    }
    .layout--twocol.layout--background-size--contain.layout--column-gap--added.layout--column-width--50-50.layout--align-items--start .grid--layout--suite .layout__region--first {
        margin-bottom: 0;
    }
    @media screen and (max-width: 720px) {
        .layout--background--light-grey.layout--top-padding--xl {
            padding-top: 2.5rem !important;
        }
        .layout--background--light-grey.layout--bottom-padding--xl {
            padding-bottom: 2.5rem !important;
        }
    }

    /* Custom External Links */
    .externalCustomLink {
        border-bottom: 0;
    }
    .externalCustomLink span {
        border-bottom: 1px solid currentColor;
    }

    /* Custom Our People Profile Template */
    .ourPeopleProfiles .mobileView {
        display: none;
    }

    .ourPeopleProfiles .desktopView {
        display: block;
    }

    @media only screen and (max-width: 769px) {
        .ourPeopleProfiles .mobileView {
            display: block;
            padding-bottom: 30px;
        }

        .ourPeopleProfiles .desktopView {
            display: none;
        }
    }

    .ourPeopleProfiles .profile-article__description {
        padding-top: 1rem;
        white-space: nowrap;
    }

    /* Global Profile Template */
    .profile-pad {
        padding-top: 1rem;
    }

    .profile-article__description {
        padding-top: 1rem;
    }

    .profile-card__avatar img {
        border-radius: 7.5rem;
    }

    /* Global Video */
    video {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
    }

    /* Testimonial Split Custom */
    .padContent {
        padding-right: 1rem;
        padding-left: 1rem;
        padding-bottom: 0;
    }

    .testimonial_split {
        padding-top: 30px;
    }

    .testimonial_split ul .quote {
        margin-bottom: 0px;
    }

    /* Description below masthead */
    .descriptionTxt {
        width: 70%;
    }

    @media only screen and (max-width: 769px) {
        .descriptionTxt {
            width: 100%;
        }
    }

    /* Custom CoLEAD Banner on non template page*/
    .coleadBanner {
        padding: 64px 0px 64px 0px;
    }
    @media only screen and (max-width: 769px) {
        .coleadBanner {
            padding: 20px 0px 20px 0px;
        }
    }

    @media (min-width: 57.5rem) {
        .horizontal-tabs .horizontal-tabs-list li {
            -webkit-box-flex: 0;
            -ms-flex: 0 0 16%;
            flex: 0 0 16%;
        }
    }

	.background__color--dark-grey::after {
		background-color: #323232;
	}

	.align-self-center {
		padding: 5rem;
	}
	@media screen and (max-width: 920px)
	{
		.align-self-center {
			padding: 0rem !important;
		}
	}


	/* Coach and mentoring table*/
	.candmTable th:first-child {
		width: 20%;
	}
	.candmTable th:nth-child(2) {
		width: 10%;
	}
	.candmTable th:nth-child(3) {
		width: 23%;
	}
	.candmTable th:nth-child(4) {
		width: 47%;
	}

	@media only screen and (max-width: 769px) {
        .candmTable th {
			width: 12rem!important;
		}
    }

