* {
        box-sizing: border-box;
    }

    .imagehalf {
        width: 50%;
        float: left;
        padding: 1rem 0.75rem;
    }
    .contenthalf {
        width: 50%;
        float: left;
        padding: 1rem 0.75rem;
    }

    /* Clearfix (clear floats) */
    .row::after {
        content: "";
        clear: both;
        display: table;
    }

    @media only screen and (max-width: 769px) {
        .imagehalf {
            width: 100%;
        }
        .contenthalf {
            width: 100%;
        }
        .reverse {
            display: flex;
            flex-wrap: wrap;
            flex-direction: column-reverse;
        }
    }

	@media only screen and (min-width: 770px) {
		.row {
			align-items: center;
			display: flex;
			justify-content: flex-start;
		}
	}

