ul.custom-list {
    display: grid;
    list-style: none;
    padding: 0;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 1rem;
}

ul.custom-list li {
    border: 1px solid #d9d9d9;
    padding: 1rem;
    font-weight: 500;
}

ul.custom-list small, #colorbox .hidden .small {
    display: block;
    color: #8c8c8c;
    font-weight: normal;
}

.hidden {
    max-width: 800px;
    padding-bottom: 1.5rem;
}

#block-stickyapplynowbutton {
        position: fixed;
        bottom: 1.5rem;
        right: 1.5rem;
        z-index: 100;
display: none;
    }
div#block-stickyapplynowbutton {
    display: none;
}
    div#block-stickyapplynowbutton .field.field--name-body.field--type-text-with-summary.field--label-.field__item.text-long {
        padding: 0 !important;
    }

   div#block-stickyapplynowbutton img {
        max-width:100px;

    }

    .adm-container {
        display: flex;
        gap: 0.5rem;
        align-items: center;
display: none;
    }
